const items = $input.all();
const triggeredAt = DateTime.now().setZone('America/Los_Angeles').toFormat('yyyy-MM-dd HH:mm:ss') + ' PT';
const rows = [];
const errors = [];
const seen = new Set();
let totalCount = 0;
let dupeCount = 0;
let emptyRefCount = 0;
for (const item of items) {
  const data = item.json;
  if (!data || data.error || data.statusCode >= 400) {
    const errMsg = data ? (data.error || data.message || 'HTTP ' + (data.statusCode || 'unknown')) : 'Empty response';
    errors.push({ json: { _error: true, errorMessage: 'API call failed: ' + errMsg, triggeredAt: triggeredAt } });
    continue;
  }
  const opportunities = Array.isArray(data.opportunities) ? data.opportunities : [];
  for (const o of opportunities) {
    totalCount++;
    const ref = o.referenceNumber || '';
    if (!ref) { emptyRefCount++; continue; }
    if (seen.has(ref)) { dupeCount++; continue; }
    seen.add(ref);
    rows.push({
      json: {
        'Unique ID': 'APC-' + ref,
        Source: 'Alberta Purchasing',
        Title: o.title || '',
        'Reference #': ref,
        Organization: o.organization || '',
        'Published Date': o.publishedDate || '',
        'Closing Date': o.closingDate || '',
        'Source URL': o.sourceUrl || '',
        'Contact Name': o.contactName || '',
        Email: o.contactEmail || '',
        Phone: o.contactPhone || '',
        Status: 'New',
        'Date Added': $now.toFormat('yyyy-MM-dd'),
        'Triggered At': triggeredAt
      }
    });
  }
}
console.log('APC Flatten: ' + rows.length + ' unique rows from ' + totalCount + ' raw (' + dupeCount + ' dupes, ' + emptyRefCount + ' empty refs dropped)');
const dataOutput = rows.length > 0 ? rows : [{ json: { _empty: true } }];
if (errors.length > 0) {
  console.log('ERROR: ' + errors.map(e => e.json.errorMessage).join('; '));
}
return dataOutput;

const REQUIRED = ['Unique ID', 'Source', 'Status', 'Date Added'];
const valid = [];

for (const item of $input.all()) {
  const missing = REQUIRED.filter(col => !item.json[col]);
  if (missing.length > 0) {
    console.log('Dropping row â€“ missing required fields: ' + missing.join(', ') +
      ' | row keys: ' + Object.keys(item.json).join(', '));
    continue;
  }
  valid.push(item);
}

if (valid.length === 0) {
  console.log('Normalize Columns: all rows dropped (0 valid)');
}

return valid.length > 0 ? valid : [];

const newItems = $('Normalize Columns').all();
const existingRows = $('Get Existing Rows').all();
const uniqueKey = 'Unique ID';

const existingIds = new Set(
  existingRows.map(r => String(r.json[uniqueKey] || '').trim()).filter(Boolean)
);

return newItems.filter(item => {
  const id = String(item.json[uniqueKey] || '').trim();
  return id && !existingIds.has(id);
});
